function Todo() {
    const [todoList, setTodoList] = useState([]);
    const [editableId, setEditableId] = useState(null);

    const [editedGender, setEditedGender] = useState("");
    const [editedOccupancy, setEditedOccupancy] = useState("");
    const [editedDistance, setEditedDistance] = useState("");
    const [editedRent, setEditedRent] = useState("");
    const [editedStart, setEditedStart] = useState("");
    const [editedDeadline, setEditedDeadline] = useState("");
    const [editedImage, setEditedImage] = useState("");


    const [newGender, setNewGender] = useState("");
    const [newOccupancy, setNewOccupancy] = useState("");
    const [newDistance, setNewDistance] = useState("");
    const [newRent, setNewRent] = useState("");
    const [newStart, setNewStart] = useState("");
    const [newDeadline, setNewDeadline] = useState("");
    const [newImage, setNewImage] = useState("");


    // Fetch tasks from database
    useEffect(() => {
        axios.get('http://127.0.0.1:3001/getTodoList')
            .then(result => {
                setTodoList(result.data)
            })
            .catch(err => console.log(err))
    }, [])

    // Function to toggle the editable state for a specific row
    const toggleEditable = (id) => {
        const rowData = todoList.find((data) => data._id === id);
        if (rowData) {
            setEditableId(id);
            setEditedGender(rowData.gender);
            setEditedOccupancy(rowData.occupancy);
            setEditedDistance(rowData.distance);
            setEditedRent(rowData.rent);
            setEditedDeadline(rowData.deadline || "");
            setEditedImage(rowData.image);
        } else {
            setEditableId(null);
            setEditedGender("");
            setEditedOccupancy("");
            setEditedDistance("");
            setEditedRent("");
            setEditedStart("");
            setEditedDeadline("");
            setEditedImage("");
        }
    };



    const puppeteer = require('puppeteer');

    async function checkWordsInPage(url) {
  // Launch the browser
  const browser = await puppeteer.launch();
  const page = await browser.newPage();
  const wordsToCheck = ["This content isn't available right now", 'Go to News Feed', 'Go back',"Visit Help Center"];
  try {
    // Navigate to the page and wait for the content to be fully loaded
    await page.goto(url, { waitUntil: 'networkidle0' }); // Wait until there are no more than 0 network connections for at least 500ms

    // Optionally, you can wait for a specific selector to ensure the page is fully rendered
    // await page.waitForSelector('#specificElement');

    // Get the page content
    const pageContent = await page.evaluate(() => document.body.innerText);
    // Check each word and log the result
    var isBroken = false;
    for (const word of wordsToCheck) {
        if (pageContent.includes(word)) {
          // If condition is true, exit the loop
          isBroken = true;
          break;
        }
      }
      if (isBroken) {
        console.log(`**IS GONE** "${url}"`);
      }
  } catch (error) {
    console.error('Error:', error);
  } finally {
    // Close the browser
    await browser.close();
  }
    }

    const toggleLink = (id, url) => {
        if (checkWordsInPage(url)) {
            axios.delete('http://127.0.0.1:3001/deleteTodoList/' + id)
            .then(result => {
                console.log(result);
                window.location.reload();
            })
            .catch(err =>
                console.log(err)
            )
    }};


    // Function to add Distance to the database
    const addTask = (e) => {
        e.preventDefault();

        // if (checkRedirect(newImage)) {
        //     alert("broken link");
        //     return;
        // }
        if (new Date(newStart) > new Date(newDeadline)) {
            alert("At least make up an end date bruh")
            return;
        }
        if (!isFacebookPostLink(newImage)) {
            alert("That is not a valid imgur link")
            return;
        }
        if (!newGender || !newOccupancy || !newDistance || !newRent || !newDeadline || !newImage) {
            alert("All fields must be filled out.");
            return;
        }

        axios.post('http://127.0.0.1:3001/addTodoList', {gender: newGender, occupancy: newOccupancy, distance: newDistance, rent: newRent, start: newStart, deadline: newDeadline, image: newImage})
            .then(res => {
                console.log(res);
                window.location.reload();
            })
            .catch(err => console.log(err));
    }

    // Function to save edited data to the database
    const saveEditedTask = (id) => {
        const editedData = {
            gender: editedGender,
            occupancy: editedOccupancy,
            distance: editedDistance,
            rent: editedRent,
            start: editedStart,
            deadline: editedDeadline,
            image: editedImage,

        };

        // If the fields are empty
        if (!editedImage || !editedGender || !editedOccupancy || !editedDistance || !editedRent || !editedDeadline) {
            alert("All fields must be filled out.");
            return;
        }

        // Updating edited data to the database through updateById API
        axios.post('http://127.0.0.1:3001/updateTodoList/' + id, editedData)
            .then(result => {
                console.log(result);
                setEditableId(null);
                setEditedGender("");
                setEditedOccupancy("");
                setEditedDistance("");
                setEditedRent("");
                setEditedStart("");
                setEditedDeadline("");

                setEditedImage(null);
                 // Clear the edited deadline
                window.location.reload();
            })
            .catch(err => console.log(err));
    }


    // Delete task from database
    const deleteTask = (id) => {
        axios.delete('http://127.0.0.1:3001/deleteTodoList/' + id)
            .then(result => {
                console.log(result);
                window.location.reload();
            })
            .catch(err =>
                console.log(err)
            )
    }

    return (
        <div className="container mt-5">
            <div className="row">
                <div className="col-md-7">
                    <h2 className="text-center">Listings</h2>
                    <div className="table-responsive">
                        <table className="table table-bordered">
                            <thead className="table-primary">
                                <tr>
                                    <th>Gender</th>
                                    <th>Occupancy</th>
                                    <th>Distance</th>
                                    <th>Rent</th>
                                    <th>Start Date</th>
                                    <th>End Date</th>
                                    <th>Facebook</th>

                                </tr>
                            </thead>
                            {Array.isArray(todoList) ? (
                                <tbody>
                                    {todoList.map((data) => (
                                        <tr key={data._id}>
                                            <td>
                                                {editableId === data._id ? (
                                                    <input
                                                        type="text"
                                                        className="form-control"
                                                        value={editedGender}
                                                        onChange={(e) => setEditedGender(e.target.value)}
                                                    />
                                                ) : (
                                                    data.gender
                                                )}
                                            </td>
                                            <td>
                                                {editableId === data._id ? (
                                                    <input
                                                        type="text"
                                                        className="form-control"
                                                        value={editedOccupancy}
                                                        onChange={(e) => setEditedOccupancy(e.target.value)}
                                                    />
                                                ) : (
                                                    data.occupancy
                                                )}
                                            </td>
                                            <td>
                                                {editableId === data._id ? (
                                                    <input
                                                        type="number"
                                                        className="form-control"
                                                        value={editedDistance}
                                                        onChange={(e) => setEditedDistance(e.target.value)}
                                                    />
                                                ) : (
                                                    data.distance
                                                )}
                                            </td>
                                            <td>
                                                {editableId === data._id ? (
                                                    <input
                                                        type="number"
                                                        className="form-control"
                                                        value={editedRent}
                                                        onChange={(e) => setEditedRent(e.target.value)}
                                                    />
                                                ) : (
                                                    data.rent
                                                )}
                                            </td>
                                            <td>
                                                {editableId === data._id ? (
                                                    <input
                                                        type="date"
                                                        className="form-control"
                                                        value={editedStart}
                                                        onChange={(e) => setEditedDeadline(e.target.value)}
                                                    />
                                                ) : (
                                                    data.start ? new Date(data.start).toLocaleDateString() : ''
                                                )}
                                            </td>
                                            <td>
                                                {editableId === data._id ? (
                                                    <input
                                                        type="date"
                                                        className="form-control"
                                                        value={editedDeadline}
                                                        onChange={(e) => setEditedDeadline(e.target.value)}
                                                    />
                                                ) : (
                                                    data.deadline ? new Date(data.deadline).toLocaleDateString() : ''
                                                )}
                                            </td>
                                            <td>
                                                <button className="btn btn-sm btn-more" onClick={() => window.open(data.image)}>
                                                    Link
                                                </button>
                                            </td>
                                            <td>
                                                {editableId === data._id ? (
                                                    <button className="btn btn-success btn-sm" onClick={() => saveEditedTask(data._id)}>
                                                        Save
                                                    </button>
                                                ) : (
                                                    <button className="btn btn-primary btn-sm" onClick={() => toggleEditable(data._id)}>
                                                        Edit
                                                    </button>
                                                )}
                                                <button className="btn btn-danger btn-sm ml-1" onClick={() => deleteTask(data._id)}>
                                                    Delete
                                                </button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            ) : (
                                <tbody>
                                    <tr>
                                        <td colSpan="4">Loading products...</td>
                                    </tr>
                                </tbody>
                            )}


                        </table>
                    </div>
                </div>
                <div className="col-md-5">
                    <h2 className="text-center">Add</h2>
                    <form className="bg-light p-4">
                        <div className="mb-3">
                            <label>Gender</label>
                            <select className="form-control"
                                type="text"
                                placeholder="Enter Gender"
                                defaultValue = "default"
                                onChange={(e) => setNewGender(e.target.value)}>
                                <option value="default" disabled>select</option>
                                <option value="coed">coed</option>
                                <option value="male">male</option>
                                <option value="female">female</option>
                            </select>
                        </div>
                        <div className="mb-3">
                            <label>Occupancy</label>
                            <select className="form-control"
                                type="text"
                                selected="Enter Occupancy"
                                defaultValue="default"
                                onChange={(e) => setNewOccupancy(e.target.value)}>
                                <option value="default" disabled>select</option>
                                <option value="single">single</option>
                                <option value="double">double</option>
                                <option value="triple">triple</option>
                                <option value="quintuple">quintuple</option>
                            </select>
                        </div>
                        <div className="mb-3">
                            <label>Distance</label>
                            <input
                                className="form-control"
                                type="number"
                                placeholder="miles to Aldrich via car"
                                onChange={(e) => setNewDistance(e.target.value)}
                            />
                        </div>
                        <div className="mb-3">
                            <label>Rent</label>
                            <input
                                className="form-control"
                                type="number"
                                placeholder="monthly payment"
                                onChange={(e) => setNewRent(e.target.value)}
                            />
                        </div>
                        <div className="mb-3">
                            <label>Start Date</label>
                            <input
                                className="form-control"
                                type="date"
                                onChange={(e) => setNewStart(e.target.value)}
                            />
                        </div>
                        <div className="mb-3">
                            <label>End Date</label>
                            <input
                                className="form-control"
                                type="date"
                                onChange={(e) => setNewDeadline(e.target.value)}
                            />
                        </div>
                        <div className="mb-3">
                            <label>Facebook PUBLIC Post Link</label>
                            <input
                                className="form-control"
                                type="text"
                                placeholder="Listings will be deleted after Facebook post is deleted"
                                onChange={(e) => setNewImage(e.target.value)}
                                // onClick={() => toggleLink(data._id, data.image)}
                            />
                        </div>



                        <button onClick={addTask} className="btn btn-success btn-sm">
                            Add Task
                        </button>
                    </form>
                </div>

            </div>
        </div>
    )
}
export default Todo;
