import axios from "axios";
import React from "react";
import { useEffect, useState } from "react";


function Todo() {
    const [todoList, setTodoList] = useState([]);
    const [editableId, setEditableId] = useState(null);

    const [editedUsername, setEditedUsername] = useState("");
    const [editedGender, setEditedGender] = useState("");
    const [editedOccupancy, setEditedOccupancy] = useState("");
    const [editedDistance, setEditedDistance] = useState("");
    const [editedRent, setEditedRent] = useState("");
    const [editedStatus, setEditedStatus] = useState("");
    const [editedStart, setEditedStart] = useState("");
    const [editedDeadline, setEditedDeadline] = useState("");

    const [newUsername, setUsername] = useState("");
    const [newGender, setNewGender] = useState("");
    const [newOccupancy, setNewOccupancy] = useState("");
    const [newDistance, setNewDistance] = useState("");
    const [newRent, setNewRent] = useState("");
    const [newStatus, setNewStatus] = useState("");
    const [newStart, setNewStart] = useState("");
    const [newDeadline, setNewDeadline] = useState("");


    // Fetch tasks from database
    useEffect(() => {
        axios.get('http://127.0.0.1:3001/getTodoList')
            .then(result => {
                setTodoList(result.data)
            })
            .catch(err => console.log(err))
    }, [])

    // Function to toggle the editable state for a specific row
    const toggleEditable = (id) => {
        const rowData = todoList.find((data) => data._id === id);
        if (rowData) {
            setEditableId(id);
            setEditedUsername(rowData.username);
            setEditedGender(rowData.gender);
            setEditedOccupancy(rowData.occupancy);
            setEditedDistance(rowData.distance);
            setEditedRent(rowData.rent);
            setEditedStart(rowData.status);
            setEditedDeadline(rowData.deadline || "");
            setEditedStatus(rowData.status);
        } else {
            setEditableId(null);
            setEditedUsername("");
            setEditedGender("");
            setEditedOccupancy("");
            setEditedDistance("");
            setEditedRent("");
            setEditedStart("");
            setEditedDeadline("");
            setEditedStatus("");
        }
    };


    // Function to add Distance to the database
    const addTask = (e) => {
        e.preventDefault();
        if (!newUsername || !newGender || !newOccupancy || !newDistance || !newRent || !newStatus || !newDeadline) {
            alert("All fields must be filled out.");
            return;
        }

        axios.post('http://127.0.0.1:3001/addTodoList', {username: newUsername, gender: newGender, occupancy: newOccupancy, distance: newDistance, rent: newRent, status: newStatus, start: newStart, deadline: newDeadline, unique: "superman"})
            .then(res => {
                console.log(res);
                window.location.reload();
            })
            .catch(err => console.log(err));
    }

    // Function to save edited data to the database
    const saveEditedTask = (id) => {
        const editedData = {
            gender: editedGender,
            occupancy: editedOccupancy,
            distance: editedDistance,
            rent: editedRent,
            start: editedStart,
            deadline: editedDeadline,
            status: editedStatus,
            unique: "superman"

        };

        // If the fields are empty
        if (!editedGender || !editedOccupancy || !editedDistance || !editedRent || !editedStatus || !editedDeadline) {
            alert("All fields must be filled out.");
            return;
        }

        // Updating edited data to the database through updateById API
        axios.post('http://127.0.0.1:3001/updateTodoList/' + id, editedData)
            .then(result => {
                console.log(result);
                setEditableId(null);
                setEditedGender("");
                setEditedOccupancy("");
                setEditedDistance("");
                setEditedRent("");
                setEditedStart("");
                setEditedDeadline("");
                setEditedStatus("");
                 // Clear the edited deadline
                window.location.reload();
            })
            .catch(err => console.log(err));
    }


    // Delete task from database
    const deleteTask = (id) => {
        alert("Hello! I am an alert box!!");
        axios.delete('http://127.0.0.1:3001/deleteTodoList/' + id)
            .then(result => {
                console.log(result);
                window.location.reload();
            })
            .catch(err =>
                console.log(err)
            )
    }

    return (
        <div className="container mt-5">
            <div className="row">
                <div className="col-md-7">
                    <h2 className="text-center">Zot Listings</h2>
                    <div className="table-responsive">
                        <table className="table table-bordered">
                            <thead className="table-primary">
                                <tr>
                                    <th>Gender</th>
                                    <th>Occupancy</th>
                                    <th>Distance</th>
                                    <th>Rent</th>
                                    <th>Start Date</th>
                                    <th>End Date</th>
                                    <th>More</th>

                                </tr>
                            </thead>
                            {Array.isArray(todoList) ? (
                                <tbody>
                                    {todoList.map((data) => (
                                        <tr key={data._id}>
                                            <td>
                                                {editableId === data._id ? (
                                                    <input
                                                        type="text"
                                                        className="form-control"
                                                        value={editedGender}
                                                        onChange={(e) => setEditedGender(e.target.value)}
                                                    />
                                                ) : (
                                                    data.gender
                                                )}
                                            </td>
                                            <td>
                                                {editableId === data._id ? (
                                                    <input
                                                        type="text"
                                                        className="form-control"
                                                        value={editedOccupancy}
                                                        onChange={(e) => setEditedOccupancy(e.target.value)}
                                                    />
                                                ) : (
                                                    data.occupancy
                                                )}
                                            </td>
                                            <td>
                                                {editableId === data._id ? (
                                                    <input
                                                        type="number"
                                                        className="form-control"
                                                        value={editedDistance}
                                                        onChange={(e) => setEditedDistance(e.target.value)}
                                                    />
                                                ) : (
                                                    data.distance
                                                )}
                                            </td>
                                            <td>
                                                {editableId === data._id ? (
                                                    <input
                                                        type="number"
                                                        className="form-control"
                                                        value={editedRent}
                                                        onChange={(e) => setEditedRent(e.target.value)}
                                                    />
                                                ) : (
                                                    data.rent
                                                )}
                                            </td>
                                            <td>
                                                {editableId === data._id ? (
                                                    <input
                                                        type="date"
                                                        className="form-control"
                                                        value={editedStart}
                                                        onChange={(e) => setEditedDeadline(e.target.value)}
                                                    />
                                                ) : (
                                                    data.start ? new Date(data.start).toLocaleDateString() : ''
                                                )}
                                            </td>
                                            <td>
                                                {editableId === data._id ? (
                                                    <input
                                                        type="date"
                                                        className="form-control"
                                                        value={editedDeadline}
                                                        onChange={(e) => setEditedDeadline(e.target.value)}
                                                    />
                                                ) : (
                                                    data.deadline ? new Date(data.deadline).toLocaleDateString() : ''
                                                )}
                                            </td>
                                            <td>
                                                <button className="btn btn-sm btn-more" onClick={() => alert(data.username+"\n"+data.status)}>
                                                    More
                                                </button>
                                            </td>
                                            {/* <td>
                                                {editableId === data._id ? (
                                                    <button className="btn btn-success btn-sm" onClick={() => saveEditedTask(data._id)}>
                                                        Save
                                                    </button>
                                                ) : (
                                                    <button className="btn btn-primary btn-sm" onClick={() => toggleEditable(data._id)}>
                                                        Edit
                                                    </button>
                                                )}
                                                <button className="btn btn-danger btn-sm ml-1" onClick={() => deleteTask(data._id)}>
                                                    Delete
                                                </button>
                                            </td> */}
                                        </tr>
                                    ))}
                                </tbody>
                            ) : (
                                <tbody>
                                    <tr>
                                        <td colSpan="4">Loading products...</td>
                                    </tr>
                                </tbody>
                            )}


                        </table>
                    </div>
                </div>
                <div className="col-md-5">
                    <h2 className="text-center">Add</h2>
                    <form className="bg-light p-4">
                        <div className="mb-3">
                            <label>Discord Username</label>
                            <input
                                className="form-control"
                                type="text"
                                placeholder="make sure to check inbox"
                                onChange={(e) => setNewRent(e.target.value)}
                            />
                        </div>
                        <div className="mb-3">
                            <label>Gender</label>
                            <select className="form-control"
                                type="text"
                                placeholder="Enter Gender"
                                onChange={(e) => setNewGender(e.target.value)}>
                                <option selected disabled>select</option>
                                <option value="coed">coed</option>
                                <option value="male">male</option>
                                <option value="female">female</option>
                            </select>
                        </div>
                        <div className="mb-3">
                            <label>Occupancy</label>
                            <select className="form-control"
                                type="text"
                                placeholder="Enter Occupancy"
                                onChange={(e) => setNewOccupancy(e.target.value)}>
                                <option selected disabled>select</option>
                                <option value="single">single</option>
                                <option value="double">double</option>
                                <option value="triple">triple</option>
                                <option value="quintuple">quintuple</option>
                            </select>
                        </div>
                        <div className="mb-3">
                            <label>Distance</label>
                            <input
                                className="form-control"
                                type="number"
                                placeholder="miles to Aldrich via car"
                                onChange={(e) => setNewDistance(e.target.value)}
                            />
                        </div>
                        <div className="mb-3">
                            <label>Rent</label>
                            <input
                                className="form-control"
                                type="number"
                                placeholder="monthly payment"
                                onChange={(e) => setNewRent(e.target.value)}
                            />
                        </div>
                        <div className="mb-3">
                            <label>Start Date</label>
                            <input
                                className="form-control"
                                type="date"
                                onChange={(e) => setNewStart(e.target.value)}
                            />
                        </div>
                        <div className="mb-3">
                            <label>End Date</label>
                            <input
                                className="form-control"
                                type="date"
                                onChange={(e) => setNewDeadline(e.target.value)}
                            />
                        </div>
                        <div className="mb-3">
                            <label>Details</label>
                            <textarea
                                className="form-control"
                                type="textarea"
                                placeholder="anything else goes here"
                                onChange={(e) => setNewStatus(e.target.value)}
                            >


                            </textarea>
                        </div>

                        <button onClick={addTask} className="btn btn-success btn-sm">
                            Add Task
                        </button>
                    </form>
                </div>

            </div>
        </div>
    )
}
export default Todo;
